function renderItems() {
    let menuItemsElement = document.getElementById("menuItems");
    menuItemsElement.innerText = '';
    html = '';
    html += addLogo();

    menuItems.forEach(item => {
        html += getMenuItem(item);

        if (item.children.length > 0 && !isMenuHide) {
            html += `<span class="dropIcon" id="menuItem-${item.label}">`;
            html += item.toggle
                ? `<svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" clip-rule="evenodd" d="M3.49999 9.33334C3.38463 9.33332 3.27188 9.29909 3.17597 9.23499C3.08006 9.1709 3.00532 9.0798 2.96118 8.97323C2.91703 8.86665 2.90548 8.74938 2.92798 8.63624C2.95048 8.5231 3.00602 8.41917 3.08757 8.33759L6.58757 4.83759C6.69696 4.72824 6.84531 4.6668 6.99999 4.6668C7.15467 4.6668 7.30301 4.72824 7.4124 4.83759L10.9124 8.33759C10.994 8.41917 11.0495 8.5231 11.072 8.63624C11.0945 8.74938 11.0829 8.86665 11.0388 8.97323C10.9947 9.0798 10.9199 9.1709 10.824 9.23499C10.7281 9.29909 10.6153 9.33332 10.5 9.33334H3.49999Z" fill="#909BA9"/>
                </svg>`
                : ` <svg width="11" height="11" viewBox="0 0 10 6" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" clip-rule="evenodd" d="M1.5 0.666656C1.38464 0.666681 1.27189 0.700906 1.17598 0.765005C1.08007 0.829105 1.00533 0.9202 0.961185 1.02677C0.917044 1.13335 0.905492 1.25062 0.92799 1.36376C0.950488 1.4769 1.00603 1.58083 1.08758 1.66241L4.58758 5.16241C4.69697 5.27176 4.84532 5.3332 5 5.3332C5.15468 5.3332 5.30302 5.27176 5.41241 5.16241L8.91241 1.66241C8.99397 1.58083 9.04951 1.4769 9.072 1.36376C9.0945 1.25062 9.08295 1.13335 9.03881 1.02677C8.99467 0.9202 8.91992 0.829105 8.82401 0.765005C8.72811 0.700906 8.61535 0.666681 8.5 0.666656H1.5Z" fill="#909BA9"/>
                </svg>`;
            if (item.toggle) {
                item.children.forEach(itemChildren => {
                    html += getMenuItem(itemChildren);
                })
            }
            html += '</span>';
        }

        html += '</li>';

    });
    menuItemsElement.innerHTML = html;
}


function toggleMenuItem(id) {
    let flag = false;
    menuItems.forEach(menuItem => {
        if (menuItem.id === id && !flag) {
            menuItem.toggle = !menuItem.toggle;
            if (menuItem.children.length) {
                menuItem.active = !menuItem.active;
            }
            flag = true;
        }
    })
    renderItems()
    return;
}

if (!isMenuHide) {
    renderItems();
    document.getElementById("menuFooter").innerHTML = addHideToggle();
}

function getMenuItem(item) {
    let _html
    if (!isMenuHide) {
        _html = `<li class="listStyleActive" onclick={toggleMenuItem(${item.id})}>`;
        if (item.active) {
            _html = `<li class="listStyleActive" onclick={toggleMenuItem(${item.id})}>`;
        } else {
            _html = `<li class="listStyle" onclick={toggleMenuItem(${item.id})}>`;
        }
    } else if (isMenuHide) {
        _html = `<li class="listStyleHidden" onclick={toggleMenuItem(${item.id})}>`;
    }

    if (item.icon != null) {
        _html += `
            <svg class="svgStyle" xmlns="http://www.w3.org/2000/svg" width="20" height="21" viewBox="0 0 20 21">
                <path fill-rule="evenodd"
                      clip-rule="evenodd"
                      d="${item.icon}"
                      fill="black"
                      fill-opacity="1"/>
            </svg>
        `;
    }

    if (!isMenuHide) {
        _html += `<span class="labelSpan">${item.label}</span>`;
        if (item.isChild && item.badgeNr) {
            _html += `<div class="badgeCircle">
                            <span class="badgeContent">${item.badgeNr}</span>
                      </div>`;
        }
    } else if (isMenuHide && item.badgeNr !== 0) {
        _html += `<span class="badgeHidden">${item.badgeNr}</span>`;
    }

    return _html;
}

function showTip(oEvent) {
    let oDiv = document.getElementById("divTip1");
    oDiv.style.visibility = "visible";
    oDiv.style.left = oEvent.clientX + 5;
    oDiv.style.top = oEvent.clientY + 5;
}

function hideTip() {
    let oDiv = document.getElementById("divTip1");
    oDiv.style.visibility = "hidden";
}



